SELECT * FROM ( SELECT rownum rn, TB.* FROM ( SELECT FIX_DEAL_SEQ , BUN_CHK , CHUL_CODE , PUM_CODE , PUM_NAME , MOK_CODE , MOK_NAME , CHUL_LEVEL , NVL(CHUL_LEVEL_CUSTOM,CHUL_LEVEL_NAME) AS CHUL_LEVEL_NAME , TWIN_CNT , BOX_CNT , SOK_CNT , GROUP_CODE , DEAL_TYPE , UNIT_PRICE , CHUL_DATE , NVL(AUC_DATE, '-') AS AUC_DATE , CASE WHEN FIX_STATE = '3' AND SYSDATE <= TO_DATE(NVL(AUC_DATE, CHUL_DATE) || ' ' || (SELECT END_TIME FROM TB_NEW_TIME WHERE BUN_CHK = 'N' AND TIME_TYPE = '3' AND DAY_TYPE = TO_CHAR(TO_DATE(NVL(AUC_DATE, CHUL_DATE),'YYYY-MM-DD'), 'd')), 'YYYY-MM-DD HH24:MI')-(SELECT END_FEW_DAY FROM TB_NEW_TIME WHERE BUN_CHK = 'N' AND TIME_TYPE = '3' AND DAY_TYPE = TO_CHAR(TO_DATE(NVL(AUC_DATE, CHUL_DATE),'YYYY-MM-DD'), 'd')) THEN '7' WHEN FIX_STATE = '3' AND SYSDATE > TO_DATE(NVL(AUC_DATE, CHUL_DATE) || ' ' || (SELECT END_TIME FROM TB_NEW_TIME WHERE BUN_CHK = 'N' AND TIME_TYPE = '3' AND DAY_TYPE = TO_CHAR(TO_DATE(NVL(AUC_DATE, CHUL_DATE),'YYYY-MM-DD'), 'd')), 'YYYY-MM-DD HH24:MI')-(SELECT END_FEW_DAY FROM TB_NEW_TIME WHERE BUN_CHK = 'N' AND TIME_TYPE = '3' AND DAY_TYPE = TO_CHAR(TO_DATE(NVL(AUC_DATE, CHUL_DATE),'YYYY-MM-DD'), 'd')) THEN '8' ELSE FIX_STATE END AS FIX_STATE , CASE WHEN FIX_STATE = '3' AND SYSDATE < TO_DATE(NVL(AUC_DATE, CHUL_DATE) || ' ' || (SELECT STR_TIME FROM TB_NEW_TIME WHERE BUN_CHK = 'N' AND TIME_TYPE = '3' AND DAY_TYPE = TO_CHAR(TO_DATE(NVL(AUC_DATE, CHUL_DATE),'YYYY-MM-DD'), 'd')), 'YYYY-MM-DD HH24:MI')-(SELECT STR_FEW_DAY FROM TB_NEW_TIME WHERE BUN_CHK = 'N' AND TIME_TYPE = '3' AND DAY_TYPE = TO_CHAR(TO_DATE(NVL(AUC_DATE, CHUL_DATE),'YYYY-MM-DD'), 'd')) THEN '9' ELSE FIX_STATE END AS FIX_STATE_TWO , AUC_BAN_TEXT , ITEM_TEXT , THUMB_IMG , LOGIN_ID , MEMBER_ID , INSERT_ID , TO_CHAR(INSERT_DATE, 'YYYY-MM-DD') AS INSERT_DATE , INSERT_DATE AS INSERT_DATE_SORT , UPDATE_ID , UPDATE_DATE FROM TB_FIX_DEAL WHERE 1=1 AND BUN_CHK = 'N' AND LOGIN_ID = #{id}
AND CHUL_DATE = #{searchStartDt}
AND CHUL_CODE LIKE '%'||#{searchChulCode}||'%'
AND ( MOK_CODE LIKE '%'||#{searchMokCode}||'%' OR MOK_NAME LIKE '%'||#{searchMokCode}||'%' )
AND ( PUM_CODE LIKE '%'||#{searchPumCode}||'%' OR PUM_NAME LIKE '%'||#{searchPumCode}||'%' )
ORDER BY INSERT_DATE_SORT DESC ) TB )

SELECT COUNT(FIX_DEAL_SEQ) AS CNT FROM TB_FIX_DEAL WHERE 1=1 AND BUN_CHK = 'N' AND LOGIN_ID = #{id}
