SELECT COUNT(FIX_DEAL_SEQ) AS CNT FROM (SELECT T2.* , (SELECT COUNT(BID_SEQ) FROM TB_FIX_BID WHERE FIX_DEAL_SEQ = T2.FIX_DEAL_SEQ AND J_CODE = #{code}) AS BID_CNT FROM TB_FIX_DEAL T2 WHERE T2.BUN_CHK = 'N' AND T2.FIX_STATE IN ('3', '4', '5', '6') AND T2.AUC_DATE BETWEEN TO_CHAR(SYSDATE-7,'YYYY-MM-DD') AND TO_CHAR(SYSDATE+7,'YYYY-MM-DD') ) T1 WHERE 1=1 AND (FIX_STATE = '3' OR (FIX_STATE IN ('4', '5', '6') AND BID_CNT > 0))
AND AUC_DATE >= #{searchStartDt}
AND AUC_DATE <= #{searchEndDt}
AND CHUL_CODE LIKE '%'||#{searchChulCode}||'%'
AND ( MOK_CODE LIKE '%'||#{searchMokCode}||'%' OR MOK_NAME LIKE '%'||#{searchMokCode}||'%' )
AND ( PUM_CODE LIKE '%'||#{searchPumCode}||'%' OR PUM_NAME LIKE '%'||#{searchPumCode}||'%' )

